        where Ord.EntityId=pTennantId and Ord.Type=0 order by Ord.OrderNum desc limit 1;

        set vHighOrder=vHighOrder+1;
        insert into DA_Order (
            EntityId,
            OrderNum,
            Type
            )
            values (
            vLastId,
            vHighOrder,
            0);

    end if;

    set vNavId=0; 
    select NavId into vNavId from DA_Nav n where n.PageId=vLastId;
    if (vNavId<1) then
        insert into DA_Nav
            (
               PageId,
               Title,
               BaseFunction,
               Target,
               UserId
               )
         values 
            (
                vLastId,
                pTitle,
                0,
                0,
                pUserId
                );
    end if;

    select p.Title, p.PageHTML, p.UpdateDate, p.PageId, p.TennantId, p.UserId, p.InlineEdit+0 as InlineEdit from DA_Pages p 
    where p.PageId=vLastId limit 1;

END$$

