INSERT INTO `DA_Pages` VALUES(57, 'About Sue', '\r\n&lt;h1&gt;&lt;/h1&gt;\r\n&lt;div&gt;For many years Sue Page have been surrounded by creative people that have inspired her to express her passion for color, fabrics, art, and furniture.&amp;nbsp; Creating fun parties for birthdays, baby showers, holidays, and \r\nprofessional company gatherings with unique themes fulled with style.&lt;/div&gt;\r\n&lt;div&gt;&amp;nbsp;&lt;/div&gt;\r\n&lt;div&gt;Once the vision is clear on what needs to be accomplished, there is a plan put in place with open communication to make sure everyone is happy with the process of change.&lt;/div&gt;\r\n&lt;div&gt;&amp;nbsp;&lt;/div&gt;\r\n&lt;div&gt;Sue Page Design can help and make people happy with ideas and a plan for action.\r\n	&lt;div&gt;&amp;nbsp;&lt;/div&gt;&lt;/div&gt;', '2013-04-13 09:03:16', 66, 66, '\0', 0, '', 0, 0, '');
