INSERT INTO `DA_Pages` VALUES(152, 'About Us', 'Complete Repair is a "jack of all trades" home repair company out of Minneapolis, MN started in 2000.&amp;nbsp; Owner Pete Wolff started Complete Repair shortly after completing a comprehensive restoration of an 1882 Victorian mansion located in Edina, MN.&amp;nbsp; During the restoration, Pete realized that his specialized skills were very much in need especially on older homes particularily in the South Minneapolis and Edina area.&amp;nbsp; Complete Repair is certified to handle lead based paint found on some older homes.&amp;nbsp; Prior to the start of Complete Repair, Pete had 45+ years of woodworking that includes cabinet making, mass birdhouse manufacturing and collaboration in the construction of a fully functional two person airplane built in 1974 which is still in use today.&lt;br /&gt;\r\n', '0000-00-00 00:00:00', 0, 78, '\0', 0, '', 0, 0, '');
