INSERT INTO `DA_Pages` VALUES(93, 'Get in Touch', '\r\n&lt;section id="contact" class="section-block" style="margin-bottom:60px;"&gt;\r\n	&lt;h1 style="position: relative;" class="section-title cke_focus"&gt;Get in Touch. &lt;small&gt;Feel Free to Contact Us, We Don''t Bite&lt;/small&gt;&lt;/h1&gt;\r\n	&lt;div class="container"&gt;\r\n		&lt;div class="row"&gt;\r\n			&lt;div class="span12"&gt;\r\n				&lt;form style="display:none;" novalidate="novalidate" method="post" action="php/contact/contact.php" id="contact-form"&gt;\r\n					&lt;div class="controls controls-row row"&gt;\r\n						&lt;div class="span6"&gt;\r\n							&lt;input type="text" placeholder="Name" class="input-block-level required" name="ContactForm[name]" /&gt;&lt;/div&gt;\r\n						&lt;div class="span6"&gt;\r\n							&lt;input type="text" placeholder="Email" class="input-block-level required email" name="ContactForm[email]" /&gt;&lt;/div&gt;&lt;/div&gt;\r\n					&lt;div class="controls controls-row row"&gt;\r\n						&lt;div class="span6"&gt;\r\n							&lt;input type="text" placeholder="URL" class="input-block-level url" name="ContactForm[url]" /&gt;&lt;/div&gt;\r\n						&lt;div class="span6"&gt;\r\n							&lt;input type="text" placeholder="Subject" class="input-block-level required" name="ContactForm[subject]" /&gt;&lt;/div&gt;&lt;/div&gt;\r\n					&lt;div class="controls"&gt;\r\n						&lt;textarea rows="4" placeholder="Message" class="required span12" name="ContactForm[message]"&gt;Message&lt;/textarea&gt;&lt;/div&gt;\r\n					&lt;div class="form-actions"&gt;\r\n						&lt;button type="submit" class="btn btn-primary"&gt;Submit&lt;/button&gt;&lt;/div&gt;\r\n				&lt;/form&gt;&lt;/div&gt;&lt;/div&gt;\r\n		&lt;div class="row"&gt;\r\n			&lt;div class="span3"&gt;\r\n				&lt;div class="sidebar"&gt;\r\n					&lt;p style="position: relative;" class="sidebar-block"&gt;&amp;nbsp;&lt;/p&gt;\r\n					&lt;h3 style="position: relative;"&gt;Let''s Meet Face to Face&lt;/h3&gt;Feel free to meet us if you have anything to discuss. We always welcome you to come to our office. \r\n					&lt;p style="position: relative;"&gt;&amp;nbsp;&lt;/p&gt;\r\n					&lt;div class="sidebar-block"&gt;\r\n						&lt;h3 style="position: relative;"&gt;Our Location&lt;/h3&gt;\r\n						&lt;p style="position: relative;"&gt;&lt;br /&gt;\r\n							Mpls, Minnesota 55391&lt;br /&gt;\r\n							&lt;a href="mailto:loring.kaveney@idocampaigns.com"&gt;loring.kaveney@idocampaigns.com&lt;/a&gt;&lt;/p&gt;&lt;/div&gt;\r\n					&lt;p style="position: relative;" class="sidebar-block"&gt;&amp;nbsp;&lt;/p&gt;\r\n					&lt;h3 style="position: relative;"&gt;We''re Social&lt;/h3&gt;\r\n					&lt;ul class="inline-list"&gt;\r\n						&lt;li&gt;&lt;a target="_blank" href="http://www.facebook.com/IDoCampaigns"&gt;FaceBook&lt;/a&gt;&lt;br /&gt;\r\n							&lt;/li&gt;\r\n						&lt;li&gt;&lt;a target="_blank" href="http://www.twitter.com/IDoCampaigns"&gt;Twitter&lt;/a&gt;&lt;/li&gt;\r\n						&lt;li&gt;\r\n							&lt;div style="background: #fff url(''https://www.chamberofcommerce.com/Portals/0/images/badges/badges03-TransparentOverWhite.gif'') no-repeat;width:144px;height:22px;*width:151px;*height:46px;line-height:10px;text-align:left;padding: 24px 0px 0px 7px;margin:0px 0 0 18px;"&gt;&lt;a style="color:#000;font-size:11px;font-weight:bold;font-family:arial;text-decoration:none;" href="http://www.chamberofcommerce.com/wayzata-mn/direct-marketing/"&gt;Direct Marketing in Wayzata&lt;/a&gt;&lt;/div&gt;&lt;/li&gt;\r\n					&lt;/ul&gt;\r\n					&lt;p style="position: relative;"&gt;&amp;nbsp;&lt;/p&gt;&lt;/div&gt;&lt;/div&gt;\r\n			&lt;div class="span9"&gt;&amp;nbsp;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;/section&gt;', '2013-06-04 19:43:37', 4, 71, '\0', 0, '', 0, 0, '');
INSERT INTO `DA_Pages` VALUES(84, 'Who we are', '\r\n&lt;section id="about" class="section-block"&gt;\r\n	&lt;h1 style="position: relative;" class="section-title"&gt;Welcome to: I DO Campaigns &lt;small&gt;Helping your organization Lead, Connect, and Grow.&lt;/small&gt;&lt;/h1&gt;\r\n	&lt;div class="container"&gt;\r\n		&lt;div class="row"&gt;\r\n			&lt;div class="span12"&gt;\r\n				&lt;p style="position: relative;" class="lead"&gt;&lt;img width="166" height="224" id="growthImg" style="float: right;" src="/DZ/71/w/Plant.gif" alt="" /&gt;&lt;/p&gt;\r\n				&lt;p style="position: relative;" class="lead cke_focus"&gt;&lt;span style="font-weight: bold;"&gt;&lt;big&gt;Lead. Connect. Grow.&lt;/big&gt;&lt;/span&gt;&lt;/p&gt;\r\n				&lt;p style="position: relative;" class="lead"&gt;"I Do Campaigns" is focused on helping you establish a leadership position for your brand in your market by connecting you with your customers and prospects while helping you grow your business.&lt;/p&gt;\r\n				&lt;p style="position: relative;" class="lead"&gt;For over ten years, our marketing professionals have been delivering effective email and social campaigns that engage your organization with your customers while advocating your brand.&lt;/p&gt;\r\n				&lt;p style="position: relative;" class="lead"&gt;Let us help you grow.&lt;/p&gt;&lt;/div&gt;&lt;/div&gt;\r\n		&lt;div class="row"&gt;\r\n			&lt;div class="span6"&gt;\r\n				&lt;h3 style="position: relative;"&gt;Facts About Us&lt;/h3&gt;\r\n				&lt;ul class="check-list"&gt;\r\n					&lt;li&gt;We provide 100% customer satisfaction&lt;br /&gt;\r\n						&lt;/li&gt;\r\n					&lt;li&gt;We are awesome and creative&lt;/li&gt;\r\n					&lt;li&gt;We drink ten cups of coffee a day&lt;/li&gt;\r\n					&lt;li&gt;We are the best company in the world&lt;/li&gt;\r\n					&lt;li&gt;We love doing our job&lt;/li&gt;\r\n					&lt;li&gt;We always strive for customer satisfaction&lt;br /&gt;\r\n						&lt;br /&gt;\r\n						&lt;/li&gt;\r\n				&lt;/ul&gt;&lt;/div&gt;\r\n			&lt;div style="display: none;" class="span6"&gt;\r\n				&lt;h3 style="position: relative;"&gt;Our Expertise&lt;/h3&gt;\r\n				&lt;div class="skill"&gt;\r\n					&lt;h6 style="position: relative;" class="name"&gt;HTML5&lt;/h6&gt;\r\n					&lt;div class="progress progress-info progress-striped progress-mini"&gt;\r\n						&lt;div style="width: 20%;" class="bar"&gt;&amp;nbsp;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;\r\n				&lt;div class="skill"&gt;\r\n					&lt;h6 style="position: relative;" class="name"&gt;JavaScript&lt;/h6&gt;\r\n					&lt;div class="progress progress-info progress-striped progress-mini"&gt;\r\n						&lt;div style="width: 40%;" class="bar"&gt;&amp;nbsp;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;\r\n				&lt;div class="skill"&gt;\r\n					&lt;h6 style="position: relative;" class="name"&gt;CSS3&lt;/h6&gt;\r\n					&lt;div class="progress progress-info progress-striped progress-mini"&gt;\r\n						&lt;div style="width: 60%;" class="bar"&gt;&amp;nbsp;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;\r\n				&lt;div class="skill"&gt;\r\n					&lt;h6 style="position: relative;" class="name"&gt;Wordpress Development&lt;/h6&gt;\r\n					&lt;div class="progress progress-info progress-striped progress-mini"&gt;\r\n						&lt;div style="width: 80%;" class="bar"&gt;&amp;nbsp;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;\r\n		&lt;!--\r\n		End About Text\r\n		--&gt;\r\n		&lt;/div&gt;&lt;/section&gt;', '2013-06-04 19:43:37', 4, 71, '\0', 0, '', 0, 0, '');
